package com.tools.entity.ftl;import java.util.LinkedList;/** * 构建关联查询sql语句使用 * select ...  * colume as columeAlias  * ... from ...  * LEFT JOIN tableCode tabelAliasCode on tabelAliasCode.joinPrimaryKey = joinOnColume * @author hanbing * */public class FtlJoinTable {		/**	 * 关联检索列字段code	 */	private LinkedList<String> colume;	/**	 * 关联检索列字段别名	 */	private LinkedList<String> columeAlias;	/**	 * 关联检索列名字	 */	private LinkedList<String> columeName;		/**	 * 表在pdm中对应的id	 */	private String oid;	/**	 * 关联检索表Code	 */	private String tableCode;	/**	 * 关联检索表Name	 */	private String tableName;	/**	 * 关联检索表别名	 */	private String tabelAliasCode;	/**	 * 关联检索表主键	 */	private String joinPrimaryKey;	/**	 * 源表关联列	 */	private String joinOnColume;	/**	 * 表格是否有关联数据 childCols	 * 用于判断生成BySearchWithJoin或BySearch	 */	private int tableHaveChildColum = 0;		public LinkedList<String> getColume() {		return colume;	}	public void setColume(LinkedList<String> colume) {		this.colume = colume;	}	public LinkedList<String> getColumeAlias() {		return columeAlias;	}	public void setColumeAlias(LinkedList<String> columeAlias) {		this.columeAlias = columeAlias;	}	public String getTableCode() {		return tableCode;	}	public void setTableCode(String tableCode) {		this.tableCode = tableCode;	}	public String getTabelAliasCode() {		return tabelAliasCode;	}	public void setTabelAliasCode(String tabelAliasCode) {		this.tabelAliasCode = tabelAliasCode;	}	public String getJoinPrimaryKey() {		return joinPrimaryKey;	}	public void setJoinPrimaryKey(String joinPrimaryKey) {		this.joinPrimaryKey = joinPrimaryKey;	}	public String getJoinOnColume() {		return joinOnColume;	}	public void setJoinOnColume(String joinOnColume) {		this.joinOnColume = joinOnColume;	}	public String getTableName() {		return tableName;	}	public void setTableName(String tableName) {		this.tableName = tableName;	}	public LinkedList<String> getColumeName() {		return columeName;	}	public void setColumeName(LinkedList<String> columeName) {		this.columeName = columeName;	}	public String getOid() {		return oid;	}	public void setOid(String oid) {		this.oid = oid;	}	public int getTableHaveChildColum() {		return tableHaveChildColum;	}	public void setTableHaveChildColum(int tableHaveChildColum) {		this.tableHaveChildColum = tableHaveChildColum;	}}